mapper.select:
SELECT problem.id, _id, difficulty , title, submission_number,accepted_number, string_agg(problem_tag.name, ',') AS tags -- 缺少my_status FROM problem LEFT JOIN problem_tags ON problem.id = problem_tags.problem_id LEFT JOIN problem_tag ON problem_tags.problemtag_id = problem_tag.id ${qwSql}
AND EXISTS ( SELECT 1 FROM problem_tags JOIN problem_tag ON problem_tags.problemtag_id = problem_tag.id WHERE problem_tags.problem_id = problem.id AND problem_tag.name = #{tag} )
GROUP BY problem.id LIMIT #{pageSize} OFFSET #{pageOffset};

SELECT problem.*, string_agg(problem_tag.name, ',') AS tags, "user".username AS created_user
FROM problem
         JOIN "user" ON problem.created_by_id = "user".id
         LEFT JOIN problem_tags ON problem.id = problem_tags.problem_id
         LEFT JOIN problem_tag ON problem_tags.problemtag_id = problem_tag.id
    ${qwSql}
GROUP BY problem.id, "user".username LIMIT #{pageSize}
OFFSET #{pageOffset};

SELECT count(*) FROM problem ${qwSql}

    AND EXISTS (
    SELECT 1
    FROM problem_tags
    JOIN problem_tag ON problem_tags.problemtag_id = problem_tag.id
    WHERE problem_tags.problem_id = problem.id
    AND problem_tag.name = #{tag}
    )

;
